def getDictMaxLayer(dictData):
    '''
    获取 字典 dict的层数
    :param dictData:
    :return: int
    '''
    return max(getDictMaxLayer(v) if isinstance(v,abc.Mapping) else 0 for v in dictData.values()) + 1


