def nestedDictMerge(string):
    addTmpDict = {}
    flag = False
    for key,val in string.items():
        if key.isdigit():
            UID = key
            addTmpDict.update({"Uid":key})

        if isinstance(val,abc.Mapping):
            for key01,val01 in val.items():
                if isinstance(val01,abc.Sequence):
                    tmpList = [v for k,v in val.items() if isinstance(v, abc.Sequence)][0]
                    flag = True
                elif isinstance(val01,abc.Mapping):
                    addTmpDict.update(val01)
                else:
                    addTmpDict.update({key01: val01})
        else:
            addTmpDict.update({key: val})
    if flag == True:
        totalDict = [dict(list(v.items()) + list(addTmpDict.items())) for v in tmpList]
        return totalDict
    elif flag == False:
        return addTmpDict

